import React from "react";

const Testimonials = () => {
  const testimonialsData = [
    {
      name: "John Doe",
      feedback: "Amazing service! Highly recommend it to everyone looking for quality work.",
      designation: "CEO, Example Corp",
      image: "https://via.placeholder.com/100",
    },
    {
      name: "Jane Smith",
      feedback: "Exceptional experience. The team was professional and delivered on time.",
      designation: "Marketing Manager, ABC Ltd",
      image: "https://via.placeholder.com/100",
    },
    {
      name: "Alice Johnson",
      feedback: "The best team I've worked with. They truly understand the needs of their clients.",
      designation: "Freelancer",
      image: "https://via.placeholder.com/100",
    },
  ];

  return (
    <div className="container my-5">
      <h2 className="text-center text-primary mb-4">What Our Clients Say</h2>
      <div className="row justify-content-center">
        {testimonialsData.map((testimonial, index) => (
          <div key={index} className="col-md-4">
            <div className="card shadow-lg border-0 rounded-4 mb-4">
              <div className="card-body text-center">
                <img
                  src={testimonial.image}
                  alt={testimonial.name}
                  className="rounded-circle mb-3"
                  style={{ width: "100px", height: "100px", objectFit: "cover" }}
                />
                <h5 className="card-title">{testimonial.name}</h5>
                <p className="text-muted mb-2">{testimonial.designation}</p>
                <p className="card-text text-secondary">"{testimonial.feedback}"</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Testimonials;
